@use '../scss' as *;
// @import url('https://fonts.googleapis.com/css2?family=DM+Serif+Display&family=Karla:wght@400;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=DM+Serif+Display&family=Karla:wght@400;500;600;700&display=swap');

// work on intermediate screens, break-points
// mobile nav could be more interesting
// adjust 'how we work' container size (mobile) 
// add comments and readme

body {
  font-family: 'Karla', sans-serif;
  font-size: $base-text;
  background: $neutral-3;
}

button {
  font-family: 'Karla', sans-serif;
  background: transparent;
  color: $neutral-3;
  border: 0.1em solid $neutral-3;
  padding: 0.7em 2.1em;
  letter-spacing: 0.1em;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'DM Serif Display', serif;
}

.clearfix::after {
  content: "";
  clear: both;
  display: table;
}

header {
  position: relative;

  .mobile-head {
    padding: 1.25em 1.5em;
  }

  .logo {
    display: inline-block;
    padding-top: 0.5em;
  }

  .nav-menu-icon {
    float: right;
    cursor: pointer;
  }

  nav {
    display: none;
    width: 100%;
    position: absolute;
    background: $neutral-1 url(../images/bg-pattern-mobile-nav.svg) bottom no-repeat;
    z-index: 1;
    background-size: 100%;
    padding: 3em 1.75em;

    ul {
      text-align: center;
      font-size: 1.35rem;
      font-weight: weight('medium');
      letter-spacing: 0.08em;
    }

    li {
      margin-bottom: 2em;
      padding: 0.7em 0;
    }

    a, a:visited {
      color: $neutral-3;
    }

    .active {
      border: 0.07em solid $neutral-3;
    }
  }

  .nav-open {
    display: block;
  }
}

.intro {
  line-height: 0;

  .hero-img-mobile {
    width: 100%;
    height: 28em;
    object-fit: cover;
  }

  .hero-img-desktop {
    display: none;
  }

  .plans {
    min-height: 30em;
    position: relative;
    background: $primary-1 url(../images/bg-pattern-intro-left-mobile.svg) left top no-repeat;
    color: $neutral-3;
    padding: 5em 1.4em 4em 1.4em;
    text-align: center;
  }

  .plans::after {
    content: '';
    background: url(../images/bg-pattern-intro-right-mobile.svg) no-repeat right 20.8em;
    position: absolute;
    width: 100%;
    height: 42em;
    top: 0;
    right: 0;
  }

  hr {
    display: none;
    border: 0.01em solid $neutral-3;
  }

  h1 {
    font-size: 2.8rem;
    font-weight: weight('regular');
    line-height: 1;
  }

  p {
    margin: 1.5em 0;
    line-height: 1.7;
  }
}

.pitch {
  margin-top: 8em;
  padding: 0 1.5em;

  hr {
    width: 41%;
    border: 0.01em solid $neutral-2;
  }

  h2 {
    font-size: 3rem;
    font-weight: weight('regular');
    text-align: center;
    margin-top: 0.6em;
    margin-bottom: 1.3em;
  }

  h4, h2 {
    color: $neutral-1;
  }

  h4 {
    font-size: 1.7rem;
    font-weight: weight('regular');
    margin-top: 1em;
    margin-bottom: 0.7em;
  }

  .selling-point {
    margin-top: 3.5em;
    text-align: center;
  }

  p {
    color: $neutral-2;
    line-height: 1.6;
  }

  .how-we-work {
    display: flex;
    flex-direction: column;
    align-items: center;
    background: $primary-1 url(../images/bg-pattern-how-we-work-mobile.svg) right -3em no-repeat;
    color: $neutral-3;
    min-height: 18em;
    margin-top: 8em;
    padding: 3.5em 2em;
  }

  h3 {
    font-size: 2.25rem;
    font-weight: weight('regular');
    text-align: center;
    line-height: 1;
    margin-bottom: auto;
  }
}

footer {
  text-align: center;
  margin-top: 11em;
  padding: 5.25em 1.35em 1em;
  background: url(../images/bg-pattern-footer-mobile.svg) top no-repeat;
  background-size: 100%;

  .social-links {
    margin-top: 1.65em;
    margin-bottom: 2.25em;

    li {
      padding: 0.1em 0.35em;
      display: inline-block;
    }
  }

  hr {
    border: 0.01em solid hsla(276, 4%, 51%, 0.47);
  }

  .site-map {
    font-size: 0.85rem;
    margin-top: 2.5em;
    letter-spacing: 0.09em;
    font-weight: weight('semi-bold');

    h5 {
      font-family: 'Karla', sans-serif;
      font-weight: weight('semi-bold');
      color: $neutral-2;
      margin: 1em 0;
      margin: 2.5em 0;
    }

    a, a:visited {
      color: $neutral-1;
    }

    li {
      margin-bottom: 0.95em;
    }
  }
}

@media (min-width: 984px) {

  body {
    overflow-x: hidden;
  }

  header {
    position: static;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5em 8em;

    .mobile-head {
      padding: 0;
    }

    .logo {
      width: 7em;
      padding: 0;
    }

    .nav-menu-icon {
      display: none;
    }

    nav {
      display: block;
      width: auto;
      position: static;
      background: none;
      padding: 0;

      ul {
        font-size: 0.84rem;
        letter-spacing: 0.1em;
      }

      li {
        margin-left: 1.75em;
        margin-bottom: 0;
        display: inline-block;  
      }

      a:link, a:visited {
        padding: 0.7em 1.8em;
        color: $neutral-2;
        font-weight: weight('semi-bold');
      }

      a:hover {
        color: $neutral-1;
      }

      .active {
        a {
          border: 0.12em solid $neutral-1;
          color: $neutral-1;
        }

        a:hover {
          background-color: $neutral-1;
          color: $neutral-3;
        }
      }
    }
  }

  .intro {
    background-color: $primary-1;
    position: relative;
    display: flex;
    justify-content: space-between;
    height: 37em;

    .plans {
      background: none;
      text-align: left;
      padding: 6em 2em 4em 8em;
      width: 60%;
    }

    button {
      cursor: pointer;
      z-index: 4;
      position: relative;
    }

    button:hover {
      background-color: $neutral-3;
      color: $primary-1;
    }

    .plans::after {
      background: url(../images/bg-pattern-intro-left-desktop.svg) no-repeat left;
      background-size: 10em;
      top: 19em;      
      left: -0.8em;
    }

    .hero-img-mobile {
      display: none;
    }

    hr {
      width: 8.8em;
      display: block;
      float: left;
      margin: 0;
    }

    h1 {
      font-size: 4.4rem;
      padding-right: 1em;
      margin-top: 0.8em;
    }

    .hero-img-desktop {
      display: block;
      height: 44em;
      width: 50%;
      object-fit: cover;
      object-position: center;
      margin-top: 6em;
      margin-right: 8em;
    }
  }

  .intro::before {
    content: '';
    position: absolute;
    width: 100%;
    height: inherit;
    background-image: url(../images/bg-pattern-intro-right-desktop.svg);
    background-position: right top;
    background-repeat: no-repeat;
    background-size: 19em;
    z-index: 2;
  }

  .pitch {
    margin-top: 20em;
    padding: 0 8em;

    hr {
      width: 8.8em;
      margin: 0;
      float: left;
      border-color: hsla(276, 4%, 51%, 0.74);  
    }

    h2 {
      text-align: left;
      margin: 0.7em 0;
      font-size: 4.6rem;
    }

    .main-pitch {
      display: flex;
      gap: 1.25em;
    }

    .selling-point {
      text-align: left;
    }

    .how-we-work {
      max-width: 100%;
      min-height: 0;
      padding: 4em 5.5em;
      flex-direction: row;
      justify-content: space-between;
    }

    button:hover {
      cursor: pointer;
      background-color: $neutral-3;
      color: $primary-1;
    }

    h3 {
      font-size: 3.25rem;
      width: 46%;
      text-align: left;
    }
  }

  footer {
    text-align: left;
    padding: 4em 8em 1em;
    background: url(../images/bg-pattern-footer-desktop.svg) top left no-repeat;
    background-size: 35%;
    margin-top: 9em;

    .logo {
      float: left;
    }

    .social-links {
      float: right;
      margin-top: 0;
      margin-bottom: 1.25em;    
    }

    .social-icon:hover {
      filter: saturate(100%) brightness(0) invert(13%) sepia(9%) saturate(960%) hue-rotate(227deg) brightness(95%) contrast(90%); 
    }

    .site-map {
      display: flex;
      justify-content: space-between;
      width: 80%;
      margin-top: 0;
    }

    .link-section {
      a:hover {
        // text-decoration: underline;
        border-bottom: 0.1em solid $neutral-1;
      }
    }
  }
}

